import * as api from "../api";

// action creator functions
export const getPosts = () => async (dispatch) => {
  try {
    const { data } = await api.fetchPosts();
  } catch (error) {
    console.log("actions posts.js", error.message);
  }
  const action = { type: "FETCH_ALL", payload: [] };
  dispatch(action);
};
